import 'package:dotted_line/dotted_line.dart';import 'package:flutter/material.dart';import 'package:flutter_auth_challenge/main.dart';import 'package:flutter_auth_challenge/screens/login_screen.dart';import 'package:flutter_auth_challenge/widgets/container_widget.dart';class SignUpScreen extends StatelessWidget {  @override  Widget build(BuildContext context) {    var size = MediaQuery.of(context).size;    return Scaffold(      body: Container(        padding: EdgeInsets.only(top: 20,bottom: 10),        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(          gradient: signupPurpleGradient,        ),        child: SingleChildScrollView(          child: Column(            children: [              Center(                child: Image.asset('assets/images/person_on_rocket.png'),              ),              Text('Hi there!',style: TextStyle(                  fontSize: 18,color: Colors.white,fontWeight: FontWeight.w400              ),),              Padding(                padding: const EdgeInsets.all(8.0),                child: Text('Let\'s Get Started',style: TextStyle(                    fontSize: 32,color: Colors.white,fontWeight: FontWeight.w900                ),),              ),              SizedBox(height: 30,),              ContainerWidget(                color: Colors.white,                widget: TextFormField(                  keyboardType: TextInputType.emailAddress,                  style: TextStyle(fontWeight: FontWeight.w500),                  decoration: InputDecoration(                    prefixIcon: SizedBox(                      child: Image.asset('assets/icons/user.png'),                    ),                    enabledBorder: OutlineInputBorder(                      borderSide: BorderSide.none,                    ),                    focusedBorder: OutlineInputBorder(                      borderSide: BorderSide.none,                    ),                    hintText: 'Username',                    hintStyle: TextStyle(fontWeight: FontWeight.w500),                  ),                ),              ),              ContainerWidget(                color: Colors.white,                widget: TextFormField(                  obscureText: true,                  style: TextStyle(fontWeight: FontWeight.w500),                  decoration: InputDecoration(                    prefixIcon: SizedBox(                      child: Image.asset('assets/icons/key.png'),                    ),                    enabledBorder: OutlineInputBorder(                      borderSide: BorderSide.none,                    ),                    focusedBorder: OutlineInputBorder(                      borderSide: BorderSide.none,                    ),                    hintText: 'Password',                    hintStyle: TextStyle(fontWeight: FontWeight.w500),                  ),                ),              ),              ContainerWidget(                color: signupDarkPurple,                widget: Text('Create an Account',style: TextStyle(                  fontWeight: FontWeight.w700,fontSize: 16,color: Colors.white,                ),),              ),              Container(                  margin: EdgeInsets.symmetric(vertical: 20),                  child: Center(                      child: Row(                        children: [                          Expanded(                              child: Container(                                child: DottedLine(                                  direction: Axis.horizontal,                                  lineLength: size.width / 2,                                  dashColor: Colors.white54,                                ),                                margin: EdgeInsets.only(                                    right: 10,                                    left: 30),                              )),                          Text(                            'Or',                            style: TextStyle(                                fontWeight: FontWeight.w500,                                color: Colors.white),                          ),                          Expanded(                              child: Container(                                margin: EdgeInsets.only(                                    left: 10,                                    right: 30),                                child: DottedLine(                                  direction: Axis.horizontal,                                  lineLength: size.width / 2,                                  dashColor: Colors.white54,                                ),                              )),                        ],                      ))),              ContainerWidget(                color: Colors.white24,                widget: GestureDetector(                    onTap: (){                      Navigator.push(context, MaterialPageRoute(                          builder: (context)=>LoginScreen()));                    },                    child: Text('Log In',style: TextStyle(                    fontWeight: FontWeight.w700,fontSize: 16,color: Colors.white                ),)),              ),            ],          ),        ),      ),    );  }}