import 'package:flutter/material.dart';class ContainerWidget extends StatelessWidget {  final Widget widget;  final Color color;  const ContainerWidget({Key key, this.widget, this.color}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(        margin: EdgeInsets.symmetric(horizontal: 30,vertical: 7),        padding: EdgeInsets.only(left: 4),        height: 74,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(50),          color: color,        ),        child: Center(          child: widget,        )    );  }}